import { Component, OnInit, Output, EventEmitter } from '@angular/core';

import { Recipe } from '../recipe.model';

@Component({
  selector: 'app-recipe-list',
  templateUrl: './recipe-list.component.html',
  styleUrls: ['./recipe-list.component.css']
})
export class RecipeListComponent implements OnInit {

  recipeItem;
  index:number;
  
  @Output() SendToParent = new EventEmitter<{name:string,description:string,img:string}>();
  recipes: Recipe[] = [
    new Recipe('Burger', 
    'A hamburger, beefburger or burger is a sandwich consisting of one or more cooked patties of ground meat, usually beef, ', 
    'data:image/jpeg;base64,'),
    new Recipe('Pizza', 
    'Chicken is the most common type of poultry in the world. Owing to the relative ease and low cost of raising them in comp', 
    'data:image/jpeg;base64,')
  ];


  RecipeToParent(event:any){
    this.SendToParent.emit({
      name: this.recipes[event].name,
      description: this.recipes[event].description,
      img: this.recipes[event].imagePath
    });
  }

  constructor() { }

  ngOnInit() {
  }

}
